INSERT INTO `wp_options` VALUES("260", "_transient_cff_p_mexicosolidaritymexicosolidarity12me", "{\"data\":[{\"id\":\"106298412746541_1052211111488595\",\"from\":{\"name\":\"Mexico Solidarity Network\",\"category\":\"Community Organization\",\"category_list\":[{\"id\":\"373543049350668\",\"name\":\"Political Organization\"}],\"id\":\"106298412746541\"},\"message\":\"Una respuesta digna por parte de los Sups Moises y Galeano.\",\"link\":\"http:\\/\\/enlacezapatista.ezln.org.mx\\/2016\\/02\\/25\\/asi-que-mejor-autoprescribanse-esto\\/\",\"name\":\"AS\\u00cd QUE MEJOR AUTOPRESCRIBANSE ESTO: \\u00ab Enlace Zapatista\",\"caption\":\"enlacezapatista.ezln.org.mx\",\"description\":\" En todo esto los \\u00fanicos terroristas son quienes han mal gobernado este pa\\u00eds desde hace m\\u00e1s de 80 a\\u00f1os. Ustedes son simplemente los lavamanos legales de esos genocidas, y, juntos, han convertido el sistema judicial en una letrina mal hecha y saturada, la bandera nacional en un rollo de papel higi\\u00e9ni\\u2026\",\"type\":\"link\",\"status_type\":\"shared_story\",\"created_time\":\"2016-02-25T23:59:25+0000\"},{\"id\":\"106298412746541_1052201118156261\",\"from\":{\"name\":\"Mexico Solidarity Network\",\"category\":\"Community Organization\",\"category_list\":[{\"id\":\"373543049350668\",\"name\":\"Political Organization\"}],\"id\":\"106298412746541\"},\"story\":\"Mexico Solidarity Network updated their profile picture.\",\"story_tags\":{\"0\":[{\"id\":\"106298412746541\",\"name\":\"Mexico Solidarity Network\",\"type\":\"page\",\"offset\":0,\"length\":25}]},\"link\":\"https:\\/\\/www.facebook.com\\/mexicosolidarity\\/photos\\/a.141905092519206.24562.106298412746541\\/1052201084822931\\/?type=3\",\"name\":\"Mexico Solidarity Network\",\"type\":\"photo\",\"status_type\":\"added_photos\",\"object_id\":\"1052201084822931\",\"created_time\":\"2016-02-25T23:24:32+0000\"},{\"id\":\"106298412746541_1052200641489642\",\"from\":{\"name\":\"Mexico Solidarity Network\",\"category\":\"Community Organization\",\"category_list\":[{\"id\":\"373543049350668\",\"name\":\"Political Organization\"}],\"id\":\"106298412746541\"},\"story\":\"Mexico Solidarity Network updated their cover photo.\",\"story_tags\":{\"0\":[{\"id\":\"106298412746541\",\"name\":\"Mexico Solidarity Network\",\"type\":\"page\",\"offset\":0,\"length\":25}]},\"link\":\"https:\\/\\/www.facebook.com\\/mexicosolidarity\\/photos\\/a.355650354478011.80761.106298412746541\\/1052200614822978\\/?type=3\",\"name\":\"Mexico Solidarity Network\'s cover photo\",\"type\":\"photo\",\"status_type\":\"added_photos\",\"object_id\":\"1052200614822978\",\"created_time\":\"2016-02-25T23:22:28+0000\"},{\"id\":\"106298412746541_1052071694835870\",\"from\":{\"name\":\"Mexico Solidarity Network\",\"category\":\"Community Organization\",\"category_list\":[{\"id\":\"373543049350668\",\"name\":\"Political Organization\"}],\"id\":\"106298412746541\"},\"story\":\"Mexico Solidarity Network shared Democracy Now!\'s post.\",\"story_tags\":{\"0\":[{\"id\":\"106298412746541\",\"name\":\"Mexico Solidarity Network\",\"type\":\"page\",\"offset\":0,\"length\":25}],\"33\":[{\"id\":\"17414523278\",\"name\":\"Democracy Now!\",\"type\":\"page\",\"offset\":33,\"length\":14}]},\"link\":\"http:\\/\\/www.democracynow.org\\/2016\\/2\\/24\\/headlines\\/mexico_charges_against_zapatista_subcomandante_marcos_ruled_invalid_20_years_later\",\"name\":\"Democracy Now!\",\"description\":\"In Mexico, a federal court has ruled criminal charges against Zapatista leader Subcomandante Marcos are no longer valid\\u2014more than two decades after they were first lodged. The Zapatistas launched an uprising on the day the North American Free Trade Agreement went into effect, January 1, 1994, declaring that NAFTA meant death to indigenous people.\",\"type\":\"link\",\"status_type\":\"shared_story\",\"created_time\":\"2016-02-25T17:06:19+0000\"},{\"id\":\"106298412746541_1052006744842365\",\"from\":{\"name\":\"Mexico Solidarity Network\",\"category\":\"Community Organization\",\"category_list\":[{\"id\":\"373543049350668\",\"name\":\"Political Organization\"}],\"id\":\"106298412746541\"},\"message\":\"When it comes to 43 humans still missing from Ayotzinapa and the complicity of the ruling parties, \\\"better late than never\\\" can not apply.\",\"link\":\"http:\\/\\/www.theguardian.com\\/world\\/2016\\/feb\\/24\\/mexico-president-visits-city-43-missing-students-ayotzinapa\",\"name\":\"17 months later: Mexico president visits site of 43 students\' disappearance\",\"caption\":\"theguardian.com\",\"description\":\"In first visit to Iguala, Enrique Pe\\u00f1a Nieto makes only passing references to probable massacre of Ayotzinapa students in 2014\",\"type\":\"link\",\"status_type\":\"shared_story\",\"created_time\":\"2016-02-25T14:38:35+0000\"},{\"id\":\"106298412746541_1051624318213941\",\"from\":{\"name\":\"Mexico Solidarity Network\",\"category\":\"Community Organization\",\"category_list\":[{\"id\":\"373543049350668\",\"name\":\"Political Organization\"}],\"id\":\"106298412746541\"},\"message\":\"AUSM Mexico Spring 2016 (Week 5): \\\"Let\'s talk about Agency, let\'s talk about You and Me!\\\"\",\"story\":\"Mexico Solidarity Network added 3 new photos \\u2014 with Tony Nelson.\",\"story_tags\":{\"0\":[{\"id\":\"106298412746541\",\"name\":\"Mexico Solidarity Network\",\"type\":\"page\",\"offset\":0,\"length\":25}],\"52\":[{\"id\":\"10103081088615735\",\"name\":\"Tony Nelson\",\"type\":\"user\",\"offset\":52,\"length\":11}]},\"link\":\"https:\\/\\/www.facebook.com\\/mexicosolidarity\\/photos\\/a.117940808248968.7073.106298412746541\\/1051623564880683\\/?type=3\",\"name\":\"Photos from Mexico Solidarity Network\'s post\",\"type\":\"photo\",\"status_type\":\"added_photos\",\"object_id\":\"1051623564880683\",\"created_time\":\"2016-02-24T17:46:22+0000\"},{\"id\":\"106298412746541_1045258822183824\",\"from\":{\"name\":\"Mexico Solidarity Network\",\"category\":\"Community Organization\",\"category_list\":[{\"id\":\"373543049350668\",\"name\":\"Political Organization\"}],\"id\":\"106298412746541\"},\"message\":\"The Papa comes to Chiapas: \\u201cThe visit is going to give an opportunity for everything in Chiapas that\\u2019s simmering under the surface to boil over,\\u201d said diocesan spokesman Jesuit Father Pedro Arriaga. \\u201cIt\\u2019s going to again show that the Zapatista movement is still here, that indigenous marginalisation continues, that poverty persists, that [government] health clinics are very deficient. All of this will come out into the open.\\u201d\",\"link\":\"http:\\/\\/www.theguardian.com\\/world\\/2016\\/feb\\/05\\/pope-francis-mexico-visit-chiapas-indigenous-people-zapatistas\",\"name\":\"Indigenous struggle in Chiapas will come \'out in the open\' for Pope Francis\'s visit\",\"caption\":\"theguardian.com\",\"description\":\"The pope\\u2019s plans to address legacy of violence, discrimination and poverty in southern Mexican state is bound to rouse Mayan people \\u2013 and the Zapatistas\",\"type\":\"link\",\"status_type\":\"shared_story\",\"created_time\":\"2016-02-11T20:53:27+0000\"},{\"id\":\"106298412746541_220402698311250\",\"from\":{\"name\":\"Mexico Solidarity Network\",\"category\":\"Community Organization\",\"category_list\":[{\"id\":\"373543049350668\",\"name\":\"Political Organization\"}],\"id\":\"106298412746541\"},\"story\":\"Mexico Solidarity Network added an event.\",\"story_tags\":{\"0\":[{\"id\":\"106298412746541\",\"name\":\"Mexico Solidarity Network\",\"type\":\"page\",\"offset\":0,\"length\":25}],\"35\":[{\"id\":\"220402698311250\",\"name\":\"Resistance in Tlaxcala, Mexico: Popular Struggles Against Capitalism\",\"type\":\"event\",\"offset\":35,\"length\":5}]},\"link\":\"https:\\/\\/www.facebook.com\\/events\\/220402698311250\\/\",\"name\":\"Resistance in Tlaxcala, Mexico: Popular Struggles Against Capitalism\",\"caption\":\"Resistance in Tlaxcala, Mexico: Popular Struggles Against Capitalism\",\"description\":\"Resistance in Tlaxcala, Mexico: Popular Struggles Against Capitalism with Luz Rivera Mart\\u00ednez, Organizer with the Consejo Nacional Urbano y Campesino (CNUC)\\n\\nLuz will present on popular resistance to neoliberal capitalism in one of Mexico\'s smallest states, Tlaxcala. Luz has 20 years of experience constructing autonomy, organizing outside the electoral system, and resisting genetically modified corn while protecting millennia-old varieties. Her talk will have important lessons for anyone interested in human rights, women\'s, peasant, and labor movements.\",\"type\":\"event\",\"status_type\":\"created_event\",\"object_id\":\"220402698311250\",\"created_time\":\"2016-02-09T19:41:53+0000\"},{\"id\":\"106298412746541_1083361015049979\",\"from\":{\"name\":\"Mexico Solidarity Network\",\"category\":\"Community Organization\",\"category_list\":[{\"id\":\"373543049350668\",\"name\":\"Political Organization\"}],\"id\":\"106298412746541\"},\"story\":\"Mexico Solidarity Network added an event.\",\"story_tags\":{\"0\":[{\"id\":\"106298412746541\",\"name\":\"Mexico Solidarity Network\",\"type\":\"page\",\"offset\":0,\"length\":25}],\"35\":[{\"id\":\"1083361015049979\",\"name\":\"Resistance in Tlaxcala, Mexico: Popular Struggles Against Capitalism\",\"type\":\"event\",\"offset\":35,\"length\":5}]},\"link\":\"https:\\/\\/www.facebook.com\\/events\\/1083361015049979\\/\",\"name\":\"Resistance in Tlaxcala, Mexico: Popular Struggles Against Capitalism\",\"caption\":\"Resistance in Tlaxcala, Mexico: Popular Struggles Against Capitalism\",\"description\":\"Resistance in Tlaxcala, Mexico: Popular Struggles Against Capitalism with Luz Rivera Mart\\u00ednez, Organizer with the Consejo Nacional Urbano y Campesino (CNUC)\\n\\nLuz will present on popular resistance to neoliberal capitalism in one of Mexico\'s smallest states, Tlaxcala. Luz has 20 years of experience constructing autonomy, organizing outside the electoral system, and resisting genetically modified corn while protecting millennia-old varieties. Her talk will have important lessons for anyone interested in human rights, women\'s, peasant, and labor movements.\",\"type\":\"event\",\"status_type\":\"created_event\",\"object_id\":\"1083361015049979\",\"created_time\":\"2016-02-09T19:35:27+0000\"},{\"id\":\"106298412746541_1043753012334405\",\"from\":{\"name\":\"Mexico Solidarity Network\",\"category\":\"Community Organization\",\"category_list\":[{\"id\":\"373543049350668\",\"name\":\"Political Organization\"}],\"id\":\"106298412746541\"},\"message\":\"If you are in Amherst, come learn about the Uni\\u00f3n de Vecinos y Damnificados 19 Septiembre (UVyD 19) and how they used people power to recuperate lost housing after the 1985 Mexico City earthquake. Luis Saracho of UVyD and Alejandro Monz\\u00f3n of MSN will be at Hampshire College today at 4pm in the Dakin Living Room and then at Smith College at 7pm in the Campus Center.\",\"message_tags\":{\"257\":[{\"id\":\"79369506158\",\"name\":\"Hampshire College\",\"type\":\"page\",\"offset\":257,\"length\":17}],\"325\":[{\"id\":\"33342046512\",\"name\":\"Smith College\",\"type\":\"page\",\"offset\":325,\"length\":13}]},\"type\":\"status\",\"status_type\":\"mobile_status_update\",\"created_time\":\"2016-02-08T15:03:16+0000\"},{\"id\":\"106298412746541_1680934725456574\",\"from\":{\"name\":\"Mexico Solidarity Network\",\"category\":\"Community Organization\",\"category_list\":[{\"id\":\"373543049350668\",\"name\":\"Political Organization\"}],\"id\":\"106298412746541\"},\"story\":\"Mexico Solidarity Network added an event.\",\"story_tags\":{\"0\":[{\"id\":\"106298412746541\",\"name\":\"Mexico Solidarity Network\",\"type\":\"page\",\"offset\":0,\"length\":25}],\"35\":[{\"id\":\"1680934725456574\",\"name\":\"HOUSING IS A HUMAN RIGHT! The Dignified Fight for Homes in Mexico City & Beyond\",\"type\":\"event\",\"offset\":35,\"length\":5}]},\"link\":\"https:\\/\\/www.facebook.com\\/events\\/1680934725456574\\/\",\"name\":\"HOUSING IS A HUMAN RIGHT! The Dignified Fight for Homes in Mexico City & Beyond\",\"caption\":\"HOUSING IS A HUMAN RIGHT! The Dignified Fight for Homes in Mexico City & Beyond\",\"description\":\"====================\\nevento ser\\u00e1 biling\\u00fce: Luis hablar\\u00e1 en espa\\u00f1ol con traducci\\u00f3n consecutiva\\n====================\\n\\nJoin Luis Saracho de Mar\\u00eda y Campos as he discusses his work with the Uni\\u00f3n de Vecinos y Damnificados 19 de Septiembre (UVyD 19), a neighborhood organization that arose from the necessity to build tenant power to recuperate destroyed housing from the infamous September 19th, 1985 earthquake in Mexico City. UVyD 19 continues to organize to defend their human right to housing. Like many neighborhoods in the largest cities in the US, UVyD 19 resides in neighborhoods that face heavy gentrification. Discover how Mexico City tenants organize people power to fight eviction and displacement.\\n\\nLearn about the network of resistance in Mexico City of which UVyD is part. UVyD is an organization adherent to the Zapatista\'s Sixth Declaration and are therefore committed to organizing \\\"from below and to the left\\\"; in other words, organizing the have-nots against the forces of capitalism and for collective, democratic control over the issues that affect their lives.\\n\\nOrganized by the Mexican Solidarity Network & the Autonomous University of Social Movements\\n-----------------------------------------------------------------------\\nA sliding-scale suggested donation of $5\\/$10\\/$15.\\nNo one turned away for lack of funds.\",\"type\":\"event\",\"status_type\":\"created_event\",\"object_id\":\"1680934725456574\",\"created_time\":\"2016-02-08T01:54:51+0000\"},{\"id\":\"106298412746541_1041408279235545\",\"from\":{\"name\":\"Mexico Solidarity Network\",\"category\":\"Community Organization\",\"category_list\":[{\"id\":\"373543049350668\",\"name\":\"Political Organization\"}],\"id\":\"106298412746541\"},\"message\":\"Great interview with the author of Compa\\u00f1eras. \\\"The Zapatista project of indigenous autonomy still provides a model of local and regional alternatives to global capitalism. The Zapatistas still play an important role supporting and inspiring other social movements.\\\" - Hilary Klein\",\"link\":\"http:\\/\\/inthesetimes.com\\/article\\/18812\\/zapatista-women-ezln-subcomandante-marcos-companeras-hilary-klein\",\"name\":\"The Lessons of Zapatista Women Activists for Today\\u2019s Social Movements\",\"caption\":\"inthesetimes.com\",\"description\":\"The role of indigenous women in the Zapatista movement is little known.\",\"type\":\"link\",\"status_type\":\"shared_story\",\"created_time\":\"2016-02-03T16:48:47+0000\"}],\"paging\":{\"previous\":\"https:\\/\\/graph.facebook.com\\/v2.3\\/106298412746541\\/posts?fields=id,from,message,message_tags,story,story_tags,link,source,name,caption,description,type,status_type,object_id,created_time&limit=12&locale=en_US&since=1456444765&access_token=944793728885704|XJ6QqKK8Ldsssr4n5Qrs2tVr7rs&__paging_token=enc_AdAArGMh1IAmkGwI2fZBQDTTIHqvss6JxrGXjMBGX6ljFp1t6muZAyLXy1QbXzXAWp5r15Ah8k04SUcr1w44jKaflEvIQ0LfkKZBdZCGpm0llyvEngZDZD&__previous=1\",\"next\":\"https:\\/\\/graph.facebook.com\\/v2.3\\/106298412746541\\/posts?fields=id,from,message,message_tags,story,story_tags,link,source,name,caption,description,type,status_type,object_id,created_time&limit=12&locale=en_US&access_token=944793728885704|XJ6QqKK8Ldsssr4n5Qrs2tVr7rs&until=1454518127&__paging_token=enc_AdCZBZBXR7Ar9P10HDdJkUZCXhzjZC8r631eNZAUGMtH31m9zPhZCYjnwxjB7ZAr5Saaj8NL6ks2UN8W5xS5a5gGl7jx3bRys8q7Eg91hUAvCLcJlvDewZDZD\"}}", "no");
